import React, { useState, useRef, useEffect } from 'react';
import ChatMessage from './ChatMessage';
import ChatFooter from './ChatFooter';
import MessageInput from './MessageInput';
import CustomerSupportHeader from './CustomerSupportHeader';

const ChatContainer = () => {
  const [messages, setMessages] = useState([
    {
      content: "Hi there! I'm your virtual assistant. How can I help you today?",
      sender: 'assistant',
      timestamp: '19:53'
    }
  ]);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = (content) => {
    if (!content.trim()) return;

    // Add user message
    setMessages(prev => [
      ...prev,
      {
        content,
        sender: 'user',
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }
    ]);

    // Show typing indicator
    setIsTyping(true);

    // Simulate AI response
    setTimeout(() => {
      setIsTyping(false);
      setMessages(prev => [
        ...prev,
        {
          content: "Thank you for your message. Our AI assistant is processing your request. How else can I help you?",
          sender: 'assistant',
          timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }
      ]);
    }, 1500);
  };

  return (
    <div className="chat-container flex flex-col w-full rounded-xl overflow-hidden flex-grow">
      <CustomerSupportHeader />

      <div className="flex-grow overflow-y-auto p-4 no-scrollbar bg-opacity-20 bg-black">
        {messages.map((message, index) => (
          <ChatMessage
            key={index}
            content={message.content}
            sender={message.sender}
            timestamp={message.timestamp}
          />
        ))}

        {isTyping && (
          <div className="flex items-center space-x-2 p-3 max-w-xs rounded-lg assistant-message animate-pulse">
            <div className="typing-indicator flex space-x-1">
              <span className="w-2 h-2 rounded-full"></span>
              <span className="w-2 h-2 rounded-full"></span>
              <span className="w-2 h-2 rounded-full"></span>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <MessageInput onSendMessage={handleSendMessage} />
      <ChatFooter />
    </div>
  );
};

export default ChatContainer;

