import React from 'react';

const ChatMessage = ({ content, sender, timestamp }) => {
  const isAssistant = sender === 'assistant';

  return (
    <div className={`flex ${isAssistant ? 'justify-start' : 'justify-end'} mb-4`}>
      <div className={`message p-3 rounded-xl ${isAssistant ? 'assistant-message' : 'user-message'}`}>
        <div className="break-words">{content}</div>
        <div className={`text-xs mt-1 ${isAssistant ? 'text-gray-300' : 'text-blue-200'}`}>
          {timestamp}
        </div>
      </div>
    </div>
  );
};

export default ChatMessage;

files.src/components/MessageInput.jsx:
import React, { useState } from 'react';

const MessageInput = ({ onSendMessage }) => {
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim()) {
      onSendMessage(message);
      setMessage('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="chat-input p-4">
      <div className="relative flex items-center">
        <input
          type="text"
          placeholder="Ask me anything..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="w-full p-3 pr-10 rounded-full focus:outline-none"
        />
        <button 
          type="submit"
          className="absolute right-3 p-1 rounded-full text-blue-300 hover:text-blue-100"
          disabled={!message.trim()}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
          </svg>
        </button>
      </div>
    </form>
  );
};

export default MessageInput;

